var proxyMap = {
	"fiddler": require("./fiddler/fiddler.js"),
	"node": require("./node/proxy.js")
}

module.exports = {
	useProxy: function(config) {
		var active = config.proxy.active;
		if (proxyMap[active]) {
			proxyMap[active](config);
		} else {
			console.log("[info] missing proxy!");
		}
	}
}