var colors = require("colors");

var weinre = require("./lib/debug/weinre.js");
var handlerServer = require("./lib/handlerServer/handlerServer.js");
var proxy = require("./lib/proxy/proxy.js");

var configValidator = require("./lib/configValidator/configValidator.js");

module.exports = function(config) {

	// validate config
	configValidator.validate(config);

	// launch weinre and chrome
	weinre.launch(config);

	// use proxy
	proxy.useProxy(config);

	// create handle server
	handlerServer.createServer(config);
}